          repeat: -1,
          paused: true,
          onRepeat: () => {
            !isHovered && tl.pause();
          },
        });

        document.querySelector(target).addEventListener("mouseover", () => {
          isHovered = true;
          first && eval(animationSet);
          first = false;
          tl.play();
        });
        document.querySelector(target).addEventListener("mouseout", () => {
          isHovered = false;
        });
